from flask import Flask,render_template,requestimport pickleimport numpy as npmodel = pickle.load(open('model_pickle', 'rb'))app = Flask(__name__)@app.route('/')def man():    return render_template("home.html")@app.route('/predict', methods=['POST'])def home():    int_features = [int(x) for x in request.form.values()]    final_features = [np.array(int_features)]    prediction = model.predict(final_features)          return render_template('result.html', data=prediction)if __name__ == "__main__":    app.run(debug=True)